import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleLoadData = async () => {
    setLoading(true);
    setMessage('Data update process started...');

    try {
      // Trigger the backend to start the data loading process
      const response = await axios.post('http://127.0.0.1:8000/load_data');

      // If successful, update message with success status
      setMessage('Data has been updated successfully!');
    } catch (error) {
      // If there is an error, update message with error status
      setMessage('Failed to update data. Please try again.',error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>MongoDB Data Loader</h1>
      <button onClick={handleLoadData} disabled={loading}>
        {loading ? 'Loading...' : 'Load Data'}
      </button>
      <p>{message}</p>
    </div>
  );
}

export default App;
