import React, { useState } from "react";
import './App.css';

function App() {
    const [message, setMessage] = useState("");
    const [question, setQuestion] = useState("");
    const [answer, setAnswer] = useState("");

    const loadData = async () => {
        try {
            const response = await fetch("http://127.0.0.1:8000/load_client", {
                method: "POST",
            });
            const result = await response.json();
            console.log(result);  // Log the full response to check for any issues
            setMessage(result.message);  // Show success or failure message
        } catch (error) {
            console.error("Error:", error);  // Log any errors that occur
            setMessage("Failed to update data. Please try again.");
        }
    };
    const askQuestion = async () => {
        try {
            const response = await fetch("http://127.0.0.1:8000/ask_question/", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ question }),
            });
            const result = await response.json();
            if (result.answer) {
                setAnswer(result.answer);
            } else {
                setAnswer("Sorry, I couldn't find an answer.");
            }
        } catch (error) {
            setAnswer("Failed to get the answer. Please try again.");
        }
    };
    return (
        <div className="App">
            <button onClick={loadData}>Load Data into MongoDB</button>
            <p>{message}</p> {/* Display the message from the API response */}

            <div>
                <input
                    type="text"
                    value={question}
                    onChange={(e) => setQuestion(e.target.value)}
                    placeholder="Ask a question about the data"
                />
                <button onClick={askQuestion}>Ask Question</button>
            </div>
            <p>{answer}</p> {/* Display the AI's response */}
        </div>
    );
}

export default App;
